mapper.update:
UPDATE tbl_shop
SET email = #{shopEmail}, comp_name = #{compName}, comp_num = #{compNum}, comp_ceo = #{compCeo}, comp_call = #{compCall}, comp_addr1 = #{compAddr1}, comp_addr2 = #{compAddr2}, comp_intro = #{compIntro} 
WHERE no = #{shopNo}

mapper.select:
SELECT * FROM tbl_shop_time

tbl_shop_time.shop_no = #{shopNo}
AND day = #{timeDay}

ORDER BY day ASC, hour ASC

SELECT * FROM tbl_shop_table
WHERE tbl_shop_table.shop_no = #{shopNo}
ORDER BY number ASC, name ASC

mapper.update:
UPDATE tbl_shop
SET email = #{shopEmail}, comp_name = #{compName}, comp_addr2 = #{compAddr2}, comp_call = #{compCall}, reg_date = #{shopRegDate} 
WHERE no = #{shopNo}

UPDATE tbl_shop
SET view_count = view_count + 1
WHERE no = #{shopNo}
